  	DROP TABLE IF EXISTS analysis_project_words_${analysisProjectId};
	CREATE TABLE analysis_project_words_${analysisProjectId} (
  		`id` bigint(20) NOT NULL AUTO_INCREMENT,
  		`word` varchar(200) DEFAULT NULL,
  		`nature` varchar(50) DEFAULT NULL,
  		`frequency` int(11) DEFAULT 1,
  		`create_time` datetime DEFAULT CURRENT_TIMESTAMP,
  		PRIMARY KEY (`id`)
	) ENGINE=InnoDB  DEFAULT CHARSET=utf8;


  	DROP TABLE IF EXISTS analysis_project_words_${analysisProjectId}_frequency;
	CREATE TABLE analysis_project_words_${analysisProjectId}_frequency (
  		`id` bigint(20) NOT NULL AUTO_INCREMENT,
  		`word` varchar(200) DEFAULT NULL,
  		`nature` varchar(50) DEFAULT NULL,
  		`frequency` int(11) DEFAULT 1,
  		`create_time` datetime DEFAULT CURRENT_TIMESTAMP,
  		PRIMARY KEY (`id`)
	) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

